CREATE TABLE "contribution"."answer_legi_references" ("answer_id" UUID NOT NULL, "article_id" text NOT NULL, PRIMARY KEY ("answer_id","article_id") , FOREIGN KEY ("answer_id") REFERENCES "contribution"."answers"("id") ON UPDATE cascade ON DELETE cascade, FOREIGN KEY ("article_id") REFERENCES "public"."legi_articles"("id") ON UPDATE restrict ON DELETE restrict, UNIQUE ("answer_id", "article_id"));COMMENT ON TABLE "contribution"."answer_legi_references" IS E'Références du code du travail (legi) liées aux réponses';
