INSERT INTO model.models (id, created_at, updated_at, title, meta_title,type, file_name, file_size, description, preview_html) VALUES ('c45e7c8e-a2ad-456e-853c-1d2acb4f6ee9', '2022-06-07T22:00:00.000Z', '2022-06-07T22:00:00.000Z', 'Convocation à un entretien préalable à une sanction disciplinaire', 'document', 'convocation_entretien_prealable_sanction_disciplinaire.docx', 17679, 'L’employeur qui envisage de prononcer une sanction disciplinaire ayant une incidence sur la présence dans l''entreprise, la fonction, la carrière ou la rémunération du salarié doit convoquer ce dernier à un entretien préalable (ex : rétrogradation, mutation disciplinaire). Ce modèle reprend l’ensemble des mentions devant figurer dans la lettre de convocation. L’employeur pourra notifier, de façon motivée, la sanction retenue à l’encontre du salarié au plus tôt 2 jours ouvrables et au plus tard un mois après l’entretien.', '<div class="courrier-expediteur"><p><span class="editable">« Société »</span></p><p><span class="editable">« Prénom Nom du représentant »</span></p><p><span class="editable">« Fonction (DRH, etc.) »</span></p><p><span class="editable">« Adresse »</span></p><p><span class="editable">« Code postal + Ville »</span></p></div><div class="courrier-destinataire"><p><span class="editable">« Prénom Nom du salarié »</span></p><p><span class="editable">« Adresse »</span></p><p><span class="editable">« Code postal + Ville »</span></p></div><p>Lettre recommandée avec accusé de réception n° <span class="editable">« 1A XXX XXX XXX X »</span> / Lettre remise en main propre contre décharge</p><p>A <span class="editable">« lieu »</span>, le <span class="editable">« date »</span></p><p><em>Objet : Convocation à un entretien préalable à une sanction disciplinaire</em> </p><p><span class="editable">« Madame/Monsieur »</span>, </p><p>Nous vous informons que nous envisageons de prendre une sanction disciplinaire à votre encontre. </p><p>Afin de recueillir vos explications sur les faits qui vous sont reprochés, nous vous prions de bien vouloir vous présenter à l’adresse suivante le <span class="editable">« date de l’entretien »</span> à <span class="editable">« heure de l’entretien »</span> : </p><p><span class="editable">« Lieu précis de l’entretien »</span></p><p>Au cours de cet entretien, vous aurez la possibilité de vous faire assister par une personne de votre choix appartenant au personnel de l’entreprise.</p><p>Veuillez agréer, <span class="editable">« Madame / Monsieur »</span> l’expression de ma considération distinguée.</p><div class="courrier-signature"><p><span class="editable">« Prénom Nom du représentant »</span></p><p><span class="editable">« Signature »</span></p></div>');
INSERT INTO model.models_legi_references (letter_id, article_id) VALUES ('c45e7c8e-a2ad-456e-853c-1d2acb4f6ee9', 'LEGIARTI000025560074');
INSERT INTO model.models_legi_references (letter_id, article_id) VALUES ('c45e7c8e-a2ad-456e-853c-1d2acb4f6ee9', 'LEGIARTI000018536889');
INSERT INTO model.models (id, created_at, updated_at, title, meta_title,type, file_name, file_size, description, preview_html) VALUES ('d130446c-dcf0-49c9-80d6-f8fdcae10a89', '2022-06-07T22:00:00.000Z', '2022-06-07T22:00:00.000Z', 'Rupture anticipée du CDD suite à une embauche en CDI', 'document', 'rupture_anticipee_du_CDD_suite_a_une_embauche_en_CDI.docx', 19746, 'Le salarié peut rompre par anticipation son contrat de travail à durée déterminée (CDD) s’il justifie de son embauche en contrat à durée indéterminée (CDI), et seulement en CDI.\n\nEn principe, le départ du salarié devra intervenir à l’issue d’un préavis calculé de la manière suivante :\n- si le CDD prévoit une date de fin : 1 jour ouvré par semaine selon la durée totale du contrat (renouvellement(s) compris)\n- si le CDD ne prévoit pas de date de fin : 1 jour ouvré par semaine selon la durée déjà effectuée\n\nLa durée du préavis ne pourra pas excéder 2 semaines.\n\nCependant, l’employeur et le salarié peuvent en décider autrement et fixer une durée plus courte ou prévoir un départ sans préavis.', '<div class="courrier-expediteur"><p><span class="editable">« Prénom Nom du salarié »</span></p><p><span class="editable">« Adresse »</span></p><p><span class="editable">« Code postal + Ville »</span></p></div><div class="courrier-destinataire"><p><span class="editable">« Société »</span></p><p><span class="editable">« Prénom Nom du représentant »</span></p><p><span class="editable">« Fonction (DRH, etc.) »</span></p><p><span class="editable">« Adresse »</span></p><p><span class="editable">« Code postal + Ville »</span></p></div><p>Lettre recommandée avec accusé de réception n° <span class="editable">« 1A XXX XXX XXX X »</span> / Lettre remise en main propre contre décharge</p><p>À <span class="editable">« lieu »</span>, le <span class="editable">« date »</span></p><p><em>Objet : Rupture anticipée du CDD suite à une embauche en CDI</em></p><p><span class="editable">« Madame / Monsieur »</span>,</p><p><span class="options">[Dans tous les cas]</span></p><p>Du fait de mon embauche en CDI, je souhaite par la présente vous signifier ma décision de rompre mon contrat de travail par anticipation. (joindre une copie de la lettre d''engagement ou du contrat de travail)</p><p><span class="options">[Option 1 : si le salarié souhaite exécuter son préavis]</span></p><p>Mon départ sera effectif à l’expiration de mon préavis.</p><p><span class="options">[Option 2 : si le salarié souhaite négocier son préavis]</span></p><p>Bien que mon départ soit conditionné au respect d’un préavis, je souhaiterais négocier une date de départ anticipée et ainsi être libéré de l’exécution de <span class="editable">« la totalité / d’une partie »</span> de celui-ci.</p><p><span class="options">[Dans tous les cas]</span></p><p>À l’expiration de mon contrat de travail, je vous demanderai de bien vouloir rendre accessibles les documents suivants : certificat de travail, reçu pour solde de tout compte, attestation Pôle Emploi.</p><p>Je me tiens à votre disposition pour déterminer une date de départ effective au regard de ma situation.</p><p>Veuillez agréer, <span class="editable">« Madame / Monsieur »</span><em> </em>l’expression de ma considération distinguée.</p><div class="courrier-signature"><p><span class="editable">« Prénom Nom du salarié »</span></p><p><span class="editable">« Signature »</span></p></div>');
INSERT INTO model.models_legi_references (letter_id, article_id) VALUES ('d130446c-dcf0-49c9-80d6-f8fdcae10a89', 'LEGIARTI000031087476');
