update documents
set "document" = jsonb_set("document", '{highlight}','{"title": "A noter : les informations données sur cette page sont applicables depuis le 01/01/2024","content": "Cette convention collective est entrée en vigueur le 01/01/2024 suite au regroupement des conventions locales de la métallurgie ainsi que de la convention spécifique aux ingénieurs et cadres qui étaient applicables jusqu’au 31/12/2023.", "searchInfo": "Nouvelle convention collective en vigueur issue du regroupement des conventions locales de la métallurgie ainsi que de la convention spécifique aux ingénieurs et cadres"}', true)
where "source" = 'conventions_collectives'
and split_part(slug, '-', 1) = '3248'