CREATE TABLE "infographic"."infographic" ("id" uuid NOT NULL DEFAULT gen_random_uuid(), "created_at" timestamptz NOT NULL DEFAULT now(), "updated_at" timestamptz NOT NULL DEFAULT now(), "display_date" date NOT NULL, "title" text NOT NULL, "description" text NOT NULL, "meta_title" text NOT NULL, "meta_description" text NOT NULL, "svg" uuid NOT NULL, "pdf" uuid NOT NULL, "transcription" text NOT NULL, PRIMARY KEY ("id") , FOREIGN KEY ("svg") REFERENCES "public"."files"("id") ON UPDATE restrict ON DELETE restrict, FOREIGN KEY ("pdf") REFERENCES "public"."files"("id") ON UPDATE restrict ON DELETE restrict, UNIQUE ("id"));COMMENT ON TABLE "infographic"."infographic" IS E'Contient les pages infographies du CDTN';
